import React from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import SwipeableViews from 'react-swipeable-views';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Zoom from '@material-ui/core/Zoom';
import Fab from '@material-ui/core/Fab';

import { green } from '@material-ui/core/colors';
import Box from '@material-ui/core/Box';
import Login from './Login';
import Register from './Register';
import RegisterFormH from './RegisterFormH';
import Register2 from './Register2';
import RegisterForm2 from './RegisterFormI';

import LoginForm from './LoginForm';


function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <Typography
      component="div"
      role="tabpanel"
      hidden={value !== index}
      id={`action-tabpanel-${index}`}
      aria-labelledby={`action-tab-${index}`}
      {...other}
    >
      {value === index && <Box p={3}>{children}</Box>}
    </Typography>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `action-tab-${index}`,
    'aria-controls': `action-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.paper,
    width: '100%',
    position: 'relative',
    minHeight: 200,
  },
  fab: {
    display: 'flex',
    justifyContent: "center",
    alignItems: 'center',

    bottom: theme.spacing(2),


  },

}));

export default function FloatingActionButtonZoom() {
  const classes = useStyles();
  const theme = useTheme();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleChangeIndex = (index) => {
    setValue(index);
  };

  const transitionDuration = {
    enter: theme.transitions.duration.enteringScreen,
    exit: theme.transitions.duration.leavingScreen,
  };

  const fabs = [
    {
      color: 'primary',
      className: classes.fab,
      icon: <Login />,
      label: 'Login',
    },
    {
      color: 'secondary',
      className: classes.fab,
      icon: <Register />,
      label: 'Register',
    },
    {
      color: 'secondary',
      className: classes.fab,
      icon: <Register2 />,
      label: 'Register',
    },


  ];

  return (
    <div className={classes.root}>

      <AppBar position="static" color="default">
        <Tabs
          value={value}
          onChange={handleChange}
          indicatorColor="primary"
          textColor="primary"

          aria-label="action tabs example"
          centered
        >
          <Tab label="Login" {...a11yProps(0)} />
          <Tab label="Hospital Registration" {...a11yProps(1)} />
          <Tab label="Individual Registration" {...a11yProps(2)} />

        </Tabs>
      </AppBar>
      <SwipeableViews
        axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
        index={value}
        onChangeIndex={handleChangeIndex}
      >
        <TabPanel value={value} index={0} dir={theme.direction}>
          <div><LoginForm /></div>
        </TabPanel>
        <TabPanel value={value} index={1} dir={theme.direction}>
          <div><RegisterFormH /></div>
        </TabPanel>
        <TabPanel value={value} index={2} dir={theme.direction}>
          <div><RegisterFormI /></div>
        </TabPanel>

      </SwipeableViews>
      {fabs.map((fab, index) => (
        <Zoom
          key={fab.color}
          in={value === index}
          timeout={transitionDuration}
          style={{
            transitionDelay: `${value === index ? transitionDuration.exit : 0}ms`,
          }}
          unmountOnExit
        >
          <div style={{
            display: 'flex',
            justifyContent: "center",
            alignItems: 'center'
          }}>
            <Fab aria-label={fab.label} className={fab.className} color={fab.color}>
              {fab.icon}
            </Fab>
          </div>
        </Zoom>
      ))}

    </div>
  );
}
